import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";

const User = () => {
  const [user, setUser] = useState({
    name: "",
   school:"",
    email: "",
    phone: "",
    webiste: ""
  });
  const { id } = useParams();
  useEffect(() => {
    loadUser();
  }, []);
  const loadUser = async () => {
    const res = await axios.get(`http://localhost:3003/users/${id}`);
    setUser(res.data);
  };
  return (
    <div className ="background-user">
    <div className="container py-4">
      <Link className="btn btn-primary"  to="/">
     Home
      </Link>
      <br></br><br></br>

      <h1 className="display-4">№{id} Name: {user.name}</h1>
      <hr />
      <br></br>
      <ul className="list-group w-50">
        <li className="list-group-item">name: {user.name}</li>
        <li className="list-group-item">school: {user.school}</li>
        <li className="list-group-item">email: {user.email}</li>
        <li className="list-group-item">phone: {user.phone}</li>
        <li className="list-group-item">Link to the race or name  : {user.website}</li>
        <br></br><br></br>
      </ul>
    </div>
    <br></br><br></br>  <br></br>
    <h2 className="info-container" >Owner: St_d05</h2>

    </div>
  );
};

export default User;
